css:
ccp-container {
  position: fixed;
  display: flex;
  border-radius: 3px;
  background-color: #2d2d2d;
  border: 1px solid #fbb604;
  flex-direction: column;
  z-index: 5;
}
ccp-container .invisible {
  display: none;
}
ccp-container ccp-triangle {
  position: absolute;
  width: 10px;
  height: 10px;
  left: calc(50% -  5px );
  bottom: calc(100% -  5px );
  box-shadow: 1px -1px 0 0 #fbb604;
  background-color: inherit;
  transform: rotate(-45deg);
}
ccp-container.down ccp-triangle {
  top: calc(100% -  5px );
  bottom: auto;
  box-shadow: -1px 1px 0 0 #fbb604;
}
ccp-overlay {
  position: absolute;
  left: 0;
  top: 0;
  background-color: rgba(0, 0, 0, 0.26);
  width: 100%;
  height: 100%;
  z-index: 1;
}
ccp-canvas {
  position: relative;
  display: flex;
  width: 240px;
  height: 125px;
  overflow: hidden;
  background-image: linear-gradient(to right, #ffffff, #ff0000);
}
ccp-handle {
  position: absolute;
  display: block;
  top: -6.5px;
  left: -6.5px;
  width: 12px;
  height: 12px;
  border: 2px solid #fbb604;
  border-radius: 50%;
  cursor: move;
  cursor: grab;
  cursor: -webkit-grab;
}
ccp-dragger {
  position: absolute;
  display: block;
  width: 1px;
  height: 1px;
}
ccp-dragger.is-pointer-down ccp-handle {
  cursor: move;
  cursor: grabbing;
  cursor: -webkit-grabbing;
}
ccp-canvas-overlay {
  cursor: crosshair;
  width: 100%;
  height: 100%;
  background-image: linear-gradient(to top, #000000, rgba(204, 154, 129, 0));
}
ccp-panel {
  display: flex;
  align-items: center;
  padding: 12px;
  width: 100%;
  border-top: 1px solid #474747;
}
ccp-panel.notop {
  border-top: none;
  padding-top: 0;
}
ccp-panel.palette {
  padding-top: 0;
  padding-left: 0;
}
ccp-panel.palette > .btn:last-child {
  margin: 8px 0 auto auto;
}
ccp-side-buttons {
  display: flex;
  flex-direction: column;
  margin-left: 4px;
}
ccp-side-buttons > .btn {
  margin-bottom: 8px;
}
ccp-swatch {
  position: relative;
  display: block;
  border: 1px solid #474747;
  cursor: pointer;
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAIAAADZF8uwAAAAGUlEQVQYV2M4gwH+YwCGIasIUwhT25BVBADtzYNYrHvv4gAAAABJRU5ErkJgggis);
}
ccp-swatch.circle {
  width: 24px;
  height: 24px;
  border-radius: 50%;
  margin: 0 4px;
}
ccp-swatch.square {
  width: 14px;
  height: 14px;
  border-radius: 3px;
  margin: 0 6px;
}
ccp-swatch-popup {
  position: absolute;
  left: -5px;
  bottom: -5px;
  background-color: #2d2d2d;
  border: 1px solid #fbb604;
  display: flex;
  flex-direction: column;
  border-radius: 2px;
  z-index: 1;
}
ccp-swatch-popup ccp-swatch.square {
  margin: 4px;
}
ccp-container-slider {
  display: flex;
  flex-direction: column;
  width: 130px;
  margin-left: auto;
}
ccp-slider {
  position: relative;
  display: flex;
  width: 100%;
  height: 11px;
  border-radius: 2px;
}
ccp-slider.hue {
  background: linear-gradient(to left, #ff0000 0%, #ffff00 17%, #00ff00 33%, #00ffff 50%, #0000ff 67%, #ff00ff 83%, #ff0000 100%);
}
ccp-slider.alpha {
  position: relative;
  margin-top: 8px;
  background-image: linear-gradient(to right, rgba(204, 154, 129, 0), #ff0000);
}
ccp-slider.alpha:before {
  position: absolute;
  top: 0;
  left: 0;
  content: '';
  height: 100%;
  width: 100%;
  border-radius: inherit;
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAIAAADZF8uwAAAAGUlEQVQYV2M4gwH+YwCGIasIUwhT25BVBADtzYNYrHvv4gAAAABJRU5ErkJgggis);
  z-index: -1;
}
ccp-slider input {
  position: absolute;
  left: -8px;
  width: calc(100% + 16px) !important;
  height: 100%;
  -webkit-appearance: none;
  background-color: transparent;
  cursor: pointer;
  z-index: 1;
}
ccp-slider input::-webkit-slider-thumb {
  -webkit-appearance: none;
  border: 1px solid #0088ff;
  height: 16px;
  width: 16px;
  border-radius: 50%;
  background: #ffffff;
  cursor: pointer;
  margin-top: 0;
}
ccp-slider input:focus {
  outline: none;
}
ccp-slider input:focus::-webkit-slider-thumb {
  border-color: #fbb604;
}
ccp-input {
  display: flex;
  width: 182px;
}
ccp-input.invisible {
  display: none;
}
ccp-input-inner {
  display: flex;
  flex-direction: column;
  flex: 1;
  text-align: center;
  margin: 0 4px;
}
ccp-input-inner:first-of-type {
  margin-left: 0;
}
ccp-input-inner > div {
  text-transform: uppercase;
  padding-top: 8px;
}
ccp-palette-inner {
  display: flex;
  width: 200px;
}
ccp-panel-inner {
  display: flex;
  flex-wrap: wrap;
}
ccp-panel-inner > ccp-swatch.square {
  margin: 10px 0 0 10px;
  flex-shrink: 0;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 1) {
  background: #f44336;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 2) {
  background: #e91e63;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 3) {
  background: #9c27b0;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 4) {
  background: #673ab7;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 5) {
  background: #3f51b5;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 6) {
  background: #2196f3;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 7) {
  background: #03a9f4;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 8) {
  background: #00bcd4;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 9) {
  background: #009688;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 10) {
  background: #4caf50;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 11) {
  background: #8bc34a;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 12) {
  background: #cddc39;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 13) {
  background: #ffeb3b;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 14) {
  background: #ffc107;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 15) {
  background: #ff9800;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 16) {
  background: #ff5722;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 17) {
  background: #795548;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 18) {
  background: #9e9e9e;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 19) {
  background: #607d8b;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 20) {
  background: #000000;
}
ccp-panel-inner.material ccp-swatch:nth-of-type( 21) {
  background: #ffffff;
}
ccp-panel-inner.material ccp-swatch:hover {
  border-color: #fbb604;
}
ccp-panel-inner.custom .btn {
  margin: 10px 0 0 10px;
}
ccp-panel-inner.invisible {
  display: none;
}
ccp-palette-popup {
  position: absolute;
  display: flex;
  flex-direction: column;
  background-color: #2d2d2d;
  bottom: 0;
  left: 0;
  height: 100%;
  width: 100%;
  z-index: 1;
}
ccp-palette-popup.invisible {
  display: none;
}
ccp-palette-popup ccp-panel {
  cursor: pointer;
}
ccp-palette-popup ccp-panel:first-of-type {
  cursor: default;
}
ccp-palette-popup ccp-panel:first-of-type > h3 {
  flex: 1;
  margin: 0;
}
ccp-palette-popup ccp-panel:hover:not(:first-of-type) {
  background-color: #fbb604;
  color: #2d2d2d;
}
ccp-palette-popup ccp-panel ccp-swatch:first-of-type {
  margin-left: auto;
}
ccp-palette-popup ccp-panel.material ccp-swatch:nth-of-type( 1) {
  background: #f44336;
}
ccp-palette-popup ccp-panel.material ccp-swatch:nth-of-type( 2) {
  background: #e91e63;
}
ccp-palette-popup ccp-panel.material ccp-swatch:nth-of-type( 3) {
  background: #9c27b0;
}
ccp-palette-popup ccp-panel.material ccp-swatch:nth-of-type( 4) {
  background: #673ab7;
}
ccp-palette-popup ccp-panel.material ccp-swatch:nth-of-type( 5) {
  background: #3f51b5;
}
ccp-palette-popup ccp-panel.custom {
  border-bottom: 1px solid #474747;
}
ccp-palette-popup ccp-panel.custom ccp-swatch {
  background: transparent;
}
